import {
  Badge,
  Button,
  Card,
  Dropdown,
  Image,
  Menu,
  Radio,
  Tooltip,
} from 'antd';
import React from 'react';
import { useLocale } from '../../../../locales';
import { IBookInfo } from '../../../bookInfo/components/bookInfo/components/Table/index.data';
import useModalStateHooks from '../../index.redux';
import {
  EyeOutlined,
  HeartOutlined,
  UserSwitchOutlined,
} from '@ant-design/icons';
import { BookListBorrow } from '../Modal/BookListBorrow';
import { useGet } from '../../../../api/request';
import { AddBookList } from '../Modal/AddBookList';
export interface IBookItem {
  item: IBookInfo;
}

const BookItem: React.FC<IBookItem> = (props) => {
  const { data: count } = useGet<{ data: any }>(
    `getCount${props.item.isbn}`,
    `/v1/bookInfo/count?isbn=${props.item.isbn}`
  );

  const { fm } = useLocale();
  const { setModalStatus, setSelectData, setSelectBookList } =
    useModalStateHooks();
  const openBorrowBookModal = () => {
    setSelectData(props.item);
    setModalStatus(true, BookListBorrow);
  };
  const openSelectBookist = () => {
    setSelectData(props.item);
    setModalStatus(true, AddBookList);
    setSelectBookList(count?.data);
  };

  return (
    <>
      <Badge.Ribbon text={<>收藏书单{count?.data.length}</>}>
        <Card
          hoverable
          style={{ width: 200 }}
          cover={
            <Image
              width={200}
              height={280}
              src={`http://localhost:8888/${props.item.photo}`}
              fallback="data:image/png;base64,"
            />
          }
          actions={[
            <Tooltip title={fm('borrow.borrowBook')}>
              <Button
                type="primary"
                shape="circle"
                onClick={openBorrowBookModal}
                icon={<UserSwitchOutlined />}
              />
            </Tooltip>,
            <Tooltip title={fm('borrow.detail')}>
              <Button type="primary" shape="circle" icon={<EyeOutlined />} />
            </Tooltip>,
            <Tooltip title={fm('borrow.love')}>
              <Button
                type="primary"
                shape="circle"
                icon={<HeartOutlined />}
                onClick={openSelectBookist}
              />
            </Tooltip>,
          ]}
        >
          <Card.Meta
            title={props.item.bookName}
            description={props.item.author}
          />
        </Card>
      </Badge.Ribbon>
    </>
  );
};
export default BookItem;
